[{"fieldname": "print_heading_template", "fieldtype": "Custom HTML", "options": "<div class="print-heading">				<h2><div>Assessment Result</div><br><small class="sub-heading">{{ doc.name }}</small>				</h2></div>"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "<!DOCTYPE html>
<html>

<head>
    <meta charset="UTF-8">
    <style>

.styled-table {
    border-collapse: collapse;
    margin: 25px 0;
    font-size: 0.9em;
    font-family: sans-serif;
    min-width: 400px;
    box-shadow: 0 0 20px rgba(0, 0, 0, 0.15);
    text-align: center;    
    border-style: double;
}
body{
    text-align: center;
    font-size: 20px;

}
table {
  width: 700px;
}
.name {
    width: 50px;
    font-size: 25px;
    text-align: right;
    padding: 10px;
    padding-right: 15px;

}
.Signature{
    font-size: 20px;
    font-weight: bold;
    text-align: right;


}
.styled-table thead tr {
    background-color: #72757563;
    text-align: center;
    direction: rtl;
    color: #000000;
    text-align: center;
    border-style: double;
}
.styled-table th,
.styled-table td {
    padding: 12px 15px;
    border-style: double;
    text-align: center;
}
.styled-table tbody tr {
    border-bottom: 1px solid #dddddd;
}

.styled-table tbody tr:nth-of-type(even) {
    background-color: #f3f3f3;
}
.styled-table tbody tr.head_grad{
    font-weight: bold;
    background-color: #03030359;
}
.styled-table tbody td.criteria {
    color: #030303;
     width: 30%; 
}
.styled-table tbody td.score {
    color: #030303;
    width: 3%;
}
.styled-table tbody tr.row_top_couurs {
    font-weight: bold;
        background-color: #ffffff;
    color: #030303;
    height:70px;
    font-size: 20px;
    text-align: center;
    margin-lift:10px
}
.styled-table tbody th.rotate {
    transform: rotate(90deg);
    /* width: 5%; */
    padding: 0px 0px;



}

    </style>
</head>

<body>
    <h2  style="text-decoration: underline;"> نتيجة  {{ doc.assessment_group }} للعام الدراسي {{ doc.academic_year }}</h2>
    <h2 style="text-decoration: underline;"> الصف  {{ doc.student_group }} </h2> 

    <table class="styled-table"  cellpadding="10" dir="rtl">
        <thead>
            <tr>
                <th  colspan="9" class="name">الأســـم  {{ doc.student_name }} </th>
            </tr>
        </thead>

        <tr class="row_top_couurs" >
            <th >اساليب التقييم</th> 

            <th class="rotate">واجبات</th>
            <th class="rotate">مواظبه</th>
            <th class="rotate">شفهي</th>
            <th class="rotate">تحريري</th>
            <th>المجموع </th>
            <th rowspan="2">التقدير</th>
        </tr>
        <tr class="head_grad">
            <td>المواد الدرسية </td>
            <td>20</td>
            <td>20</td>
            <td>20</td>
            <td>40</td>
            <td>100</td>

        </tr>
        <tbody>
            {% set student_results = frappe.db.get_list(doctype="Assessment Result",
                    filters={"student_name":doc.student_name,
                     "assessment_group":doc.assessment_group,
                     "type_test":doc.type_test,
                     "academic_term":doc.academic_term,
                }) 
            %}

            {% for criteria in doc.details %}
                    <tr class="">
                        <td class="criteria" >{{ criteria.assessment_criteria}}</td>
                             {% for result in student_results %}
                                  {% set doc_info = frappe.get_doc("Assessment Result",result) %}
                                      {% if doc_info['course'] == criteria.assessment_criteria %}
                                            {% for row in doc_info.details %}
                                                <td class="score">{{row.score}}</td>
                                            {% endfor %}
                                            <td>{{doc_info.total_score}}</td>
                                            <td>{{doc_info.grade}}</td>
                                     {% endif %}
                                     {% else %}
                                     <td>&nbsp;</td>
                            {% endfor %}
                    </tr>
            {% endfor %}    
        </tbody> 
        <tr  class="head_grad">

<td>المجموع: </td>
<th colspan="7">{{ doc.total_score }}</th>
        </tr>


        <tr>
            <td>النسبة المئوية : </td>
            <td colspan="4"> الترتيب</td>
            <td colspan="3"> التقدير </td>



        </tr>
        <tr>

            <td colspan="4" class="Signature" > 
                <p  style="text-decoration: underline;" > ملاحظات رائد الصف : </p>
                <p> ....................................................</p>
                <p> ....................................................</p>
                <p> ....................................................</p>

                <p style="text-decoration: underline;" >توقيع رائد الصف : </p>
            </td>


            <td colspan="4" class="Signature"> 
                <p  style="text-decoration: underline;" > ملاحظات ولي الامر :</p>  
                <p> ....................................................</p>
                <p> ....................................................</p>
                <p> ....................................................</p>
                <p style="text-decoration: underline;">توقيع ولي الامر :</p>
            </td>

        </tr>

    </table>
</body>

</html>"}]
